    // };

    // const showSpan = () => {
    //     if (spanish) {
    //         return spanish.map((item, index) => {
    //             return (
    //                 <React.Fragment key={index}>
    //                     {item !== 'na' ? (
    //                         <div
    //                             className={`info-row icons gloss ${stripes(
