    //                             </a>
    //                         </div>
    //                     ) : (
    //                         <div
    //                             className={`info-row icons gloss ${stripes(
    //                                 index
    //                             )}`}>
    //                             <p className='icon-input'> </p>
    //                         </div>
    //                     )}
    //                 </React.Fragment>
    //             );
    //         });
    //     }
    // };

    // const showPort = () => {
    //     if (portuguese) {
    //         return portuguese.map((item, index) => {
    //             return (
    //                 <React.Fragment key={index}>
    //                     {item !== 'na' ? (
    //                         <div
    //                             className={`info-row icons gloss ${stripes(
    //                                 index
    //                             )}`}>
    //                             <a href={item}>
    //                                 <img
    //                                     className='icon-input svg'
    //                                     alt='Portuguese Parent Letter Icon'
    //                                     src={TOCEditIcon}
    //                                 />
    //                             </a>
    //                         </div>
    //                     ) : (
    //                         <div
    //                             className={`info-row icons gloss ${stripes(
    //                                 index
    //                             )}`}>
    //                             <p className='icon-input'> </p>
    //                         </div>
    //                     )}
    //                 </React.Fragment>
    //             );
    //         });
    //     }
    // };

    // const showHaitian = () => {
    //     if (haitian) {
    //         return haitian.map((item, index) => {
    //             return (
    //                 <React.Fragment key={index}>
    //                     {item !== 'na' ? (
    //                         <div
    //                             className={`info-row icons gloss ${stripes(
    //                                 index
    //                             )}`}>
    //                             <a href={item}>
    //                                 <img
    //                                     className='icon-input svg'
    //                                     alt='Haitian Parent Letter Icon'
    //                                     src={TOCEditIcon}
    //                                 />
